(8074, 163, '_internet_article_internet_article_items_4_internet_article_items_title', 'field_5ec468272ee16'),
(8075, 163, 'internet_article_internet_article_items_4_internet_article_items_text', '<ul>\r\n 	<li>Gestalung Ihres Produkts nach UX-Methoden</li>\r\n 	<li>Schöner, moderner und wirksamer UI-Desin</li>\r\n 	<li>Interaktionstests und Interviews mit Vetretern Ihrer Zielgruppe</li>\r\n 	<li>Beratung und Re-Design Ihrer Webseite</li>\r\n</ul>'),
